// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.7;

contract Polls {
    uint public count = 0; // state variable

    struct Vote {
        bool result;
    }

    mapping(address => Vote) public votes;

    function vote(bool _result) public {
        count++;
        votes[msg.sender] = Vote(_result);
    }

    function getVotesCount() public view returns (uint) {
        return count;
    }

    // function getVotes() public view returns (Vote) {
    //     return votes.length;
    // }
}
